import React, { useState } from 'react';

function App() {
  // Инициализация состояния с массивом объектов фильмов
  const [movies, setMovies] = useState([
    { name: 'Фильм 1', year: 1999, author: 'Автор 1' },
    { name: 'Фильм 2', year: 2005, author: 'Автор 2' },
    { name: 'Фильм 3', year: 2010, author: 'Автор 3' },
  ]);

  // Состояние для хранения выбранного фильма
  const [selectedMovie, setSelectedMovie] = useState({
    name: '',
    year: '',
    author: '',
  });

  // Функция для обновления выбранного фильма
  const selectMovie = (movie) => {
    setSelectedMovie(movie);
  };

  return (
    <div>
      <h2>Список фильмов</h2>
      <ul>
        {movies.map((movie, index) => (
          <li key={index} onClick={() => selectMovie(movie)}>
            {movie.name}
          </li>
        ))}
      </ul>
      <div>
        <input
          type="text"
          placeholder="Название фильма"
          value={selectedMovie.name}
          onChange={(e) => setSelectedMovie({ ...selectedMovie, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Год выпуска"
          value={selectedMovie.year}
          onChange={(e) => setSelectedMovie({ ...selectedMovie, year: e.target.value })}
        />
        <input
          type="text"
          placeholder="Автор"
          value={selectedMovie.author}
          onChange={(e) => setSelectedMovie({ ...selectedMovie, author: e.target.value })}
        />
      </div>
    </div>
  );
}




export default App;
