import React, { useState, useEffect } from 'react';
import { addCategory, updateCategory, deleteCategory, getCategory } from './api';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function CategoryPage() {
    const [categories, setCategories] = useState([]);
    const [category, setCategory] = useState({ name: '' });
    const [isEdit, setIsEdit] = useState(false);
    const [editId, setEditId] = useState(null);

    useEffect(() => {
        fetchCategories();
    }, []);

    const fetchCategories = async () => {
        const data = await getCategory();
        setCategories(data);
    };

    const handleChange = (e) => {
        setCategory({ ...category, [e.target.name]: e.target.value });
    };

    const handleAddCategory = async () => {
        const response = await addCategory(category);
        if (response.success) {
            toast.success(response.data);
            fetchCategories();
        } else {
            toast.error(response.data);
        }
        setCategory({ name: '' });
    };

    const handleUpdateCategory = async () => {
        const response = await updateCategory(editId, category);
        if (response.success) {
            toast.success(response.data);
            fetchCategories();
        } else {
            toast.error(response.data);
        }
        setCategory({ name: '' });
        setIsEdit(false);
        setEditId(null);
    };

    const handleEdit = (category) => {
        setCategory(category);
        setIsEdit(true);
        setEditId(category.id);
    };

    const handleDelete = async (id) => {
        const response = await deleteCategory(id);
        if (response.success) {
            toast.success(response.data);
            fetchCategories();
        } else {
            toast.error(response.data);
        }
    };

    return (
        <div className="container">
            <ToastContainer />
            <h2 className="my-4">Category Management</h2>
            <div className="mb-3">
                <input
                    type="text"
                    className="form-control"
                    placeholder="Category Name"
                    name="name"
                    value={category.name}
                    onChange={handleChange}
                />
                <button
                    className="btn btn-primary mt-2"
                    onClick={isEdit ? handleUpdateCategory : handleAddCategory}
                >
                    {isEdit ? 'Update Category' : 'Add Category'}
                </button>
            </div>
            <ul className="list-group">
                {categories.map((cat) => (
                    <li key={cat.id} className="list-group-item d-flex justify-content-between align-items-center">
                        {cat.name}
                        <div>
                            <button className="btn btn-warning btn-sm me-2" onClick={() => handleEdit(cat)}>
                                Edit
                            </button>
                            <button className="btn btn-danger btn-sm" onClick={() => handleDelete(cat.id)}>
                                Delete
                            </button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
}
