import { Outlet } from "react-router-dom";
import { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Header.css';

export default function Header() {
    const navigate = useNavigate();
    const loc = useLocation();
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const logoutForm = () => {
        console.log("logout");
        sessionStorage.removeItem("user");
        setIsLoggedIn(false);
        navigate("/login");
        window.location.reload();
    };

    useEffect(() => {
        if (loc.state && loc.state.message) {
            toast(loc.state.message);
            loc.state = null;
        }

        const user = JSON.parse(sessionStorage.getItem("user"));
        if (user) {
            setIsLoggedIn(true);
        }
    }, [loc]);

    return (
        <div>
            <ToastContainer />
            <header className="navbar navbar-expand-lg navbar-light bg-light shadow-sm mb-4">
                <div className="container">
                    <a className="navbar-brand" href="/">Home</a>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarNav">
                        <ul className="navbar-nav me-auto">
                            {!isLoggedIn && (
                                <>
                                    <li className="nav-item"><a className="nav-link" href="/login">Log in</a></li>
                                    <li className="nav-item"><a className="nav-link" href="/register">Register</a></li>
                                </>
                            )}
                            <li className="nav-item"><a className="nav-link" href="/product">Product</a></li>
                        </ul>
                        {isLoggedIn && (
                            <ul className="navbar-nav ms-auto">
                                <li className="nav-item"><button className="btn btn-outline-danger" onClick={logoutForm}>Logout</button></li>
                            </ul>
                        )}
                    </div>
                </div>
            </header>
            <Outlet />
        </div>
    );
}
