import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import MovieList from '../components/MovieList';

const MoviesPage = () => {
  const [movies, setMovies] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await axios.get('http://localhost:4000/movies');
        setMovies(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    const fetchCategories = async () => {
      try {
        const response = await axios.get('http://localhost:4000/categories');
        setCategories(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchMovies();
    fetchCategories();
  }, []);

  return (
    <div >
      <h2>Категорий өту</h2>
      <Link to="/categories" className="btn btn-primary mb-2">Add categories</Link>
      <h2>Категорий құру</h2>
      <Link to="/categories/new" className="btn btn-primary mb-2">New categories</Link>
      <Form.Group controlId="category">
        <Form.Label>Category:</Form.Label>
        <Form.Control as="select" onChange={(e) => setSelectedCategory(e.target.value)}>
          <option value="">All categories</option>
          {categories.map((category) => (
            <option key={category.id} value={category.id}>{category.name}</option>
          ))}
        </Form.Control>
      </Form.Group>
      <MovieList movies={selectedCategory ? 
        movies.filter((movie) => movie.categoryId && movie.categoryId.toString() === selectedCategory) :movies
            } setMovies={setMovies} />
    </div>
  );
};

export default MoviesPage;
