import React from 'react';
import { Link } from 'react-router-dom';
import { NotificationContainer, NotificationManager } from 'react-notifications';

const HomePage = () => {
  const handleLogout = () => {
    localStorage.removeItem('token'); // Clear the authentication token
    NotificationManager.success('You have been logged out successfully.');
    // Optionally, redirect the user to the login page
  };

  return (
    <div className="container mt-5">
      <h1>Welcome to the App!</h1>
      <p>This is the homepage of your application where you can manage your movies and categories.</p>
      <Link to="/movies" className="btn btn-primary m-2">Go to Movies</Link>
      <Link to="/categories" className="btn btn-secondary m-2">Go to Categories</Link>
      <button onClick={handleLogout} className="btn btn-danger m-2">Logout</button>
      <NotificationContainer />
    </div>
  );
};

export default HomePage;
