import { Link, Outlet, useNavigate } from "react-router-dom";
import { useEffect,useState } from "react";
import { getAllPosts, getAllUsers } from "./api";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function MyPosts(){

    const navigate = useNavigate();
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        const loadPosts = async () => {
            const {success, data } = await getAllPosts();
            if(success){
                setPosts(data);
            }
            else{
                navigate("/login", {state: { message: data }});
            }
        }
        loadPosts();
    },[]);

    return(
        <div className="container bg-light mb-5 pb-5">
            <div className="row">
                    {
                        posts.map((post) => {
                            return(
                                <div className="col-sm-6"  key={post.id}>
                                <div className="card mt-4 py-3 mx-5">
                                    <div className="card-body">
                                        <h5 className="card-title"><Link className="nav-link" to={'/posts/'+post.id} >{post.title}</Link></h5>
                                        <p className="card-text"> Price : { post.price}</p>
                                    </div>
                                </div>
                                </div>
                            )
                        })
                    }
                </div>
            <div>
                <Outlet/>
            </div>
        </div>
    )
};
