import { Outlet } from "react-router-dom";
import { useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import 'bootstrap/dist/css/bootstrap.min.css';  

export default function Header() {
    const navigate = useNavigate();
    const loc = useLocation();

    const logoutForm = () => {
        console.log("logout");
        localStorage.removeItem("user");
        navigate("/login");
        window.location.reload();
    };

    useEffect(() => {
        if (loc.state) {
            toast(loc.state.message);
            loc.state = null;
        }

        const user = JSON.parse(localStorage.getItem("user"));
        // if(user)
        //     console.log("USER:" + user);
    }, [loc]);

    // const handleSearchSubmit = (event) => {
    //     event.preventDefault();
    // };

    return (
        <div>
            <ToastContainer />
            <div className="container">  
                <nav className="navbar navbar-expand-lg navbar-light bg-light">  
                    <a className="navbar-brand" href="/">Home page</a>
                    <div className="collapse navbar-collapse">
                        {/* <form className="form-inline my-2 my-lg-0" onSubmit={handleSearchSubmit}>
                            <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" />
                            <button className="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
                        </form> */}
                        <ul className="navbar-nav mr-auto">
                            <li className="nav-item"><a className="nav-link" href="/login">log in</a></li>
                            <li className="nav-item"><a className="nav-link" href="/register">Registered</a></li>
                            <li className="nav-item"><a className="nav-link" href="/addproduct">Add car</a></li>
                            <li className="nav-item"><a className="nav-link" href="//kolesa.group">Kolesa.kz</a></li>
                            <li className="nav-item"><a className="nav-link" href="/product">Cars</a></li>
                            <li className="nav-item"><a className="nav-link" href="/">Role</a></li>
                            <button className="btn btn-danger" onClick={logoutForm}>Logout</button>
                        </ul>
                    </div>
                </nav>
            </div>
            <Outlet />
        </div>
    );
}
