import { useState, useEffect } from "react";
import { deletepost,getOnePost } from './api';
import { Link, useNavigate, useParams } from "react-router-dom";
import {ToastContainer, toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function DeletePost(){

    const navigate = useNavigate();
    const params = useParams();
    const [post, setPost] = useState([]);

    useEffect(() => {
        const loadPost = async () => {
            const {success, data} = await getOnePost(params.postId);
            if(success){
                setPost(data);
            }
            else{
                navigate("/login");
            }
        }
        loadPost();
    },[params.postId]);

    const handleSubmit = async () => {
        const success = await deletepost(params.postId);
        if(success){
            toast("Ноут өшірілді!!");
            navigate('/posts/my');
        }
        else{
            toast("Post error!!");
            
        }
    };
    return(
        <div className="container">
            <h2>Post өшіріледі!!!</h2>
            <ToastContainer/>
                <h4>{post.title} өшеді???</h4>
                <button type="submit" className="btn btn-success" onClick={handleSubmit}>Ия</button> <br/>
            <Link to={'/posts/my'}>Жоқ</Link>
        </div>
    );
}

export default DeletePost;
