import React, { useState } from 'react';

const movies = [
  { name: 'Temirlan', year: 2022, author: 'John Doe' },
  { name: 'Zhanserik', year: 2021, author: 'Jane Doe' },
  { name: 'Erejep', year: 2020, author: 'Bob Smith' },
  { name: 'Almat', year: 2019, author: 'Alice Johnson' },
  { name: 'Kydyrali', year: 2018, author: 'Charlie Brown' },
  { name: 'Aikumis', year: 2017, author: 'David Lee' },
];

function MovieList() {
  const [selectedMovie, setSelectedMovie] = useState(null);

  const handleMovieClick = (movie) => {
    setSelectedMovie(movie);
  };

  return (
    <div>
      <ul>
        {movies.map((movie) => (
          <li key={movie.name} onClick={() => handleMovieClick(movie)}>
            {movie.name}
          </li>
        ))}
      </ul>
      {selectedMovie && (
        <div>
          <input type="text" value={selectedMovie.name} readOnly />
          <input type="text" value={selectedMovie.year} readOnly />
          <input type="text" value={selectedMovie.author} readOnly />
        </div>
      )}
    </div>
  );
}

export default MovieList;
