import { useEffect, useState } from "react";
import { getCategories, addCategory, updateCategory, deleteCategory } from "./api";
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Category() {
    const [categories, setCategories] = useState([]);
    const [name, setName] = useState('');
    const [isEditMode, setIsEditMode] = useState(false);
    const [editCategoryId, setEditCategoryId] = useState(null);

    useEffect(() => {
        async function fetchData() {
            const response = await getCategories();
            if (response.success) {
                setCategories(response.data);
            } else {
                toast.error("Failed to fetch categories");
            }
        }
        fetchData();
    }, []);

    const handleAdd = async () => {
        const response = await addCategory({ name });
        if (response.success) {
            setCategories([...categories, response.data]);
            setName('');
            toast.success("Category added successfully");
        } else {
            toast.error("Failed to add category");
        }
    };

    const handleEdit = (category) => {
        setIsEditMode(true);
        setEditCategoryId(category.id);
        setName(category.name);
    };

    const handleUpdate = async () => {
        const response = await updateCategory(editCategoryId, { name });
        if (response.success) {
            setCategories(categories.map(category => category.id === editCategoryId ? response.data : category));
            setName('');
            setIsEditMode(false);
            setEditCategoryId(null);
            toast.success("Category updated successfully");
        } else {
            toast.error("Failed to update category");
        }
    };

    const handleDelete = async (categoryId) => {
        const response = await deleteCategory(categoryId);
        if (response.success) {
            setCategories(categories.filter(category => category.id !== categoryId));
            toast.success("Category deleted successfully");
        } else {
            toast.error("Failed to delete category");
        }
    };

    return (
        <div className="container mt-5">
            <ToastContainer />
            <h2 className="mb-3">Categories</h2>
            <div className="row">
                <div className="col-md-6">
                    <input 
                        type="text" 
                        className="form-control mb-3" 
                        value={name} 
                        onChange={(e) => setName(e.target.value)} 
                        placeholder="Enter category name" 
                        required 
                    />
                    <button className="btn btn-primary" onClick={isEditMode ? handleUpdate : handleAdd}>
                        {isEditMode ? "Update Category" : "Add Category"}
                    </button>
                </div>
            </div>
            <div className="row mt-5">
                {categories.map(category => (
                    <div className="col-md-4 mb-3" key={category.id}>
                        <div className="card h-100">
                            <div className="card-body">
                                <h5 className="card-title">{category.name}</h5>
                                <button className="btn btn-info me-2" onClick={() => handleEdit(category)}>Edit</button>
                                <button className="btn btn-danger" onClick={() => handleDelete(category.id)}>Delete</button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}
