import React, { useState } from 'react';

const poems = ['Poem 1', 'Poem 2', 'Poem 3', 'Poem 4', 'Poem 5'];

function PoemList() {
  const [selectedPoems, setSelectedPoems] = useState([]);

  const handlePoemClick = (poem) => {
    setSelectedPoems([...selectedPoems, poem]);
  };

  return (
    <div>
      <h2>Poems</h2>
      <ul>
        {poems.map((poem) => (
          <li key={poem} onClick={() => handlePoemClick(poem)}>
            {poem}
          </li>
        ))}
      </ul>
      <h2>Selected Poems</h2>
      <ul>
        {selectedPoems.map((poem) => (
          <li key={poem}>{poem}</li>
        ))}
      </ul>
    </div>
  );
}

export default PoemList;
