import React, { useState } from 'react';

function Goods({ goods, categories, addGood, updateGood, deleteGood }) {
  const [name, setName] = useState('');
  const [categoryId, setCategoryId] = useState('');
  const [editingGood, setEditingGood] = useState(null);

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleCategoryChange = (e) => {
    setCategoryId(e.target.value);
  };

  const handleAddGood = () => {
    addGood({ name, categoryId });
    setName('');
    setCategoryId('');
  };

  const handleUpdateGood = () => {
    updateGood({ ...editingGood, name, categoryId });
    setEditingGood(null);
    setName('');
    setCategoryId('');
  };

  const handleDeleteGood = (id) => {
    deleteGood(id);
  };

  const handleEditGood = (good) => {
    setEditingGood(good);
    setName(good.name);
    setCategoryId(good.categoryId);
  };

  return (
    <div>
      <h2>Goods</h2>
      <input type="text" value={name} onChange={handleNameChange} />
      <select value={categoryId} onChange={handleCategoryChange}>
        <option value="">Select category</option>
        {categories.map((category) => (
          <option key={category.id} value={category.id}>
            {category.name}
          </option>
        ))}
      </select>
      <button onClick={editingGood ? handleUpdateGood : handleAddGood}>
        {editingGood ? 'Update Good' : 'Add Good'}
      </button>
      <ul>
        {goods.map((good) => (
          <li key={good.id}>
            {good.name} ({categories.find((category) => category.id === good.categoryId)?.name})
            <button onClick={() => handleEditGood(good)}>Edit</button>
            <button onClick={() => handleDeleteGood(good.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Goods;
