import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { NotificationManager } from 'react-notifications';

const MovieList = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await axios.get('http://localhost:4000/movies');
        setMovies(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchMovies();
  }, []);
  
  // Delete
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/movies/${id}`);
      setMovies(movies.filter((movie) => movie.id !== id));
      NotificationManager.success('Фильм успешно удален');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Жаңа Фильмдер қосу</h2>
      <Link to="/movies/new" className="btn btn-primary mb-2">New Movies</Link>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Description</th>
            <th>Category</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {movies.map((movie) => (
            <tr key={movie.id}>
              <td>{movie.id}</td>
              <td>{movie.title}</td>
              <td>{movie.description}</td>
              <td>{movie.category && movie.category.name}</td>
              <th> movie.category.name</th>
              <td>
                <Link to={`/movies/${movie.id}/edit`} className="btn btn-sm btn-warning mr-1">Edit</Link>
                <button onClick={() => handleDelete(movie.id)} className="btn btn-sm btn-danger">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default MovieList;
