import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import MovieList from '../components/MovieList';

const MoviesPage = () => {
  const [movies, setMovies] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await axios.get('http://localhost:4000/movies');
        setMovies(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    const fetchCategories = async () => {
      try {
        const response = await axios.get('http://localhost:4000/categories');
        setCategories(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchMovies();
    fetchCategories();
  }, []);

  return (
    <div>
     <div>
      <h2>Категорий өту</h2>
      <Link to="/categories" className="btn btn-primary mb-2">Add categories</Link>
      <h2>Категорий құру</h2>
      <Link to="/categories/new" className="btn btn-primary mb-2">New categories</Link></div>
      <div className="btn-group" role="group" aria-label="Category buttons">
        <button type="button" className={`btn btn-secondary ${selectedCategory === null && 'active'}`} onClick={() => setSelectedCategory(null)}>
          All categories
        </button>
        {categories.map((category) => (
          <button key={category.id} type="button" className={`btn btn-secondary ${selectedCategory === category.id && 'active'}`} onClick={() => setSelectedCategory(category.id)}>
            {category.name}
          </button>
        ))}
      </div>
      <MovieList movies={selectedCategory ? movies.filter((movie) => movie.categoryId && movie.categoryId.toString() === selectedCategory) : movies} setMovies={setMovies} />
    </div>
  );
};

export default MoviesPage;
