import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { NotificationContainer, NotificationManager } from 'react-notifications';

const AuthForm = ({ isRegister }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(`http://localhost:4000/${isRegister ? 'register' : 'login'}`, { email, password, ...(isRegister && { name }) });
      if (response.data.token) {
        localStorage.setItem('token', response.data.token);
        NotificationManager.success('Login successful!');
        navigate('/');
      } else {
        NotificationManager.success('Registration successful! Please log in.');
        navigate('/login');
      }
    } catch (error) {
      console.error(error);
      NotificationManager.error('Error during authentication');
    }
  };

  return (
    <div className="auth-form">
      <h2>{isRegister ? 'Register' : 'Login'}</h2>
      <form onSubmit={handleSubmit}>
        {isRegister && (
          <div className="form-group">
            <label htmlFor="name">Name</label>
            <input type="text" className="form-control" id="name" value={name} onChange={(e) => setName(e.target.value)} required />
          </div>
        )}
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input type="email" className="form-control" id="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input type="password" className="form-control" id="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        </div>
        <button type="submit" className="btn btn-danger">{isRegister ? 'Register' : 'Login'}</button>
      </form>
      <NotificationContainer />
    </div>
  );
};

export default AuthForm;
