import React, { useState } from "react";
import InputComp from "./InputComp";
import GameListComp from "./GameListComp";

const App = () => {
  const [games, setGames] = useState([]);

  const fetchGames = async ({ platform, category }) => {
    const url = `https://www.freetogame.com/api/games?platform=${platform}&category=${category}`;
    const response = await fetch(url);
    const data = await response.json();
    setGames(data);
  };

  return (
    <div>
     
      <InputComp onSearch={fetchGames} />
      <GameListComp games={games} />
    </div>
  );
};

export default App;
