import React, { useState } from 'react';
import ColorInput from './ColorInput';
import ImageDisplay from './ImageDisplay';

const App = () => {
  const [imageData, setImageData] = useState(null);

  const fetchImage = async (hexColor) => {
    const response = await fetch(`https://php-noise.com/noise.php?hex=${hexColor}&json`);
    const data = await response.json();
    setImageData(data.noise);
  };

  const handleSubmit = (hexColor) => {
    fetchImage(hexColor);
  };

  return (
    <div>
      <ColorInput onSubmit={handleSubmit} />
      <ImageDisplay imageData={imageData} />
    </div>
  );
};

export default App;
