import { Link, Outlet, useNavigate, useParams, useLocation } from "react-router-dom";
import { useEffect,useState } from "react";
// import { ToastContainer, toast } from "react-toastify";
import { getOneMovie, getAllUsers } from "./api";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function MovieItem(){

    const location = useLocation();
    const navigate = useNavigate();
    const params = useParams();
    const [movie, setMovie] = useState([]);
    const [hasToken, setHasToken] = useState(false);
    const [users, setUser] = useState('');

    useEffect(() => {
        if(location.state){
           // toast(location.state.message);
            location.state = null;
        }

        const user = JSON.parse(localStorage.getItem("user"));
        if(user)
            setHasToken(true);
    }, [location]);

    useEffect(() => {
        const loadMovie = async () => {
            const {success, data } = await getOneMovie(params.movieId);
            if(success){
                setMovie(data);
            }
            else{
                navigate("/login", {state: { message: data }});
            }
        }
        loadMovie();
    },[params.movieId]);
    
    useEffect(()=>{
        const loadUsers = async () => {
            const {success, data } = await getAllUsers();
            if(success){
                setUser(data);
            }
            else{
                navigate("/login", {state: { message: data }});
            }
        }
        loadUsers();
    },[]);
    return(
        <div className="container">
            <div className="card">
                <div className="card-body">
                    <h5 className="card-title">{movie.title}</h5>
                    <p className="card-text"><br/>Описание :{movie.content}<br/><br/>Price: {movie.price}<br/> </p>
                </div>
             </div>
        </div>
    );
}
