import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import { NotificationManager } from 'react-notifications';

const MovieForm = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [categoryId, setCategoryId] = useState('');
  const [categories, setCategories] = useState([]);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get('http://localhost:4000/categories');
        setCategories(response.data);
      } catch (error) {
        console.error(error);
        NotificationManager.error('Ошибка при выборе категорий');
      }
    };

    const fetchMovie = async () => {
      if (id) {
        try {
          const response = await axios.get(`http://localhost:4000/movies/${id}`);
          setTitle(response.data.title);
          setDescription(response.data.description);
          setCategoryId(response.data.categoryId);
        } catch (error) {
          console.error(error);
          NotificationManager.error('Ошибка при выборе фильма');
        }
      }
    };

    fetchCategories();
    fetchMovie();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (id) {
      // Update movie
      try {
        await axios.put(`http://localhost:4000/movies/${id}`, { title, description, categoryId });
        NotificationManager.success('Фильм успешно обновлен');
        navigate('/movies');
      } catch (error) {
        console.error(error);
        NotificationManager.error('Ошибка при обновлении фильма');
      }
    } else {
      // Create movie
      try {
        await axios.post('http://localhost:4000/movies', { title, description, categoryId });
        NotificationManager.success('Фильм успешно создан');
        navigate('/movies');
      } catch (error) {
        console.error(error);
        NotificationManager.error('Ошибка при создании фильма');
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label htmlFor="title">Title</label>
        <input type="text" className="form-control" id="title" value={title} onChange={(e) => setTitle(e.target.value)} required />
      </div>
      <div className="form-group">
        <label htmlFor="description">Description</label>
        <textarea className="form-control" id="description" value={description} onChange={(e) => setDescription(e.target.value)} required></textarea>
      </div>
      <div className="form-group">
        <label htmlFor="categoryId">Category</label>
        <select className="form-control" id="categoryId" value={categoryId} onChange={(e) => setCategoryId(e.target.value)} required>
          <option value="">Select a category</option>
          {categories.map((category) => (
            <option key={category.id} value={category.id}>{category.name}</option>
          ))}
        </select>
      </div>
      <button type="submit" className="btn btn-danger ">Save</button>
    </form>
  );
};

export default MovieForm;
