import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { NotificationManager } from 'react-notifications';


const CategoryList = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get('http://localhost:4000/categories');
        setCategories(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchCategories();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/categories/${id}`);
      setCategories(categories.filter((category) => category.id !== id));
      NotificationManager.success('Категория успешно удалена', 'Успех!', 3000, () => {
        alert('callback');
      });
    } catch (error) {
      console.error(error);
    }
  };


  return (
    <div>
      <h2>Категорий құру</h2>
      <Link to="/categories/new" className="btn btn-primary mb-2">New Category</Link>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {categories.map((category) => (
            <tr key={category.id}>
              <td>{category.id}</td>
              <td>{category.name}</td>
              <td>
                <Link to={`/categories/${category.id}/edit`} className="btn btn-sm btn-warning mr-1">Edit</Link>
                <button onClick={() => handleDelete(category.id)} className="btn btn-sm btn-danger">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CategoryList;
