import React, { useState } from 'react';

const InputComp = ({ onSubmit }) => {
  const [id, setId] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(id);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Введите ID продукта:
        <input
          type="text"
          value={id}
          onChange={(e) => setId(e.target.value)}
        />
      </label>
      <button type="submit">Получить информацию о продукте</button>
    </form>
  );
};

export default InputComp;
