import { useEffect, useState } from "react";
import { getProduct, deleteProduct } from "./api";
import { Link } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function ProductSpring() {
    const [products, setProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");

    useEffect(() => {
        getProducts();
    }, []);

    const getProducts = async () => {
        const response = await getProduct();
        setProducts(response);
    };

    const handleSearchChange = (event) => {
        setSearchTerm(event.target.value);
    };

    const handleDelete = async (id) => {
        try {
            await deleteProduct(id);
            setProducts(products.filter(prod => prod.id !== id));
        } catch (error) {
            console.log("Delete error:", error);
        }
    }

    const filteredProducts = products.filter(product =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div className="container mt-4">
            <input
                type="text"
                className="form-control mb-3"
                placeholder="Search products..."
                value={searchTerm}
                onChange={handleSearchChange}
            />
            {filteredProducts.map((prod) => (
                <div className="card mb-3" key={prod.id}>
                    <div className="row g-0">
                        <div className="col-md-4">
                            {prod.image && (
                                <img src={require(`./images/${prod.image}`)} className="img-fluid rounded-start" alt={prod.name} />
                            )}
                        </div>
                        <div className="col-md-8">
                            <div className="card-body">
                                <h5 className="card-title"><a href="/" className="text-decoration-none">{prod.name}</a></h5>
                                <p className="card-text"><small className="text-muted">{prod.price} tenge</small></p>
                                <Link to={`/update/${prod.id}`} className="btn btn-secondary"> Edit </Link>
                                <button onClick={() => handleDelete(prod.id)} className="btn btn-danger"> Delete </button>
                            </div>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
}
