import React from 'react';
import { Link } from 'react-router-dom';
import { NotificationManager } from 'react-notifications';
import axios from 'axios';


const MovieList = ({ movies,setMovies  }) => {
   // Delete
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/movies/${id}`);
      setMovies(movies.filter((movie) => movie.id !== id));
      NotificationManager.success('Фильм успешно удален');
    } catch (error) {
      console.error(error);
    }
  };
  
  return (
    <table className="table">
      <thead>
        <tr>
          <th>ID</th>
          <th>Title</th>
          <th>Description</th>
          <th>Category</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {movies.map((movie) => (
          <tr key={movie.id}>
            <td>{movie.id}</td>
            <td>{movie.title}</td>
            <td>{movie.description}</td>
            <td>{movie.category && movie.category.name}</td>
            <td>
              <Link to={`/movies/${movie.id}/edit`} className="btn btn-sm btn-warning mr-1">Edit</Link>
              <button onClick={() => handleDelete(movie.id)} className="btn btn-sm btn-danger">Delete</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default MovieList;
