import React, { useState } from 'react';

function Categories({ categories, addCategory, updateCategory, deleteCategory }) {
  const [editing, setEditing] = useState(false);
  const [category, setCategory] = useState({ name: '' });

  const handleInputChange = (e) => {
    setCategory({ ...category, [e.target.name]: e.target.value });
  };

  const handleAddCategory = (e) => {
    e.preventDefault();
    addCategory(category);
    setCategory({ name: '' });
  };

  const handleUpdateCategory = (e) => {
    e.preventDefault();
    updateCategory(category);
    setEditing(false);
  };

  const handleDeleteCategory = (id) => {
    deleteCategory(id);
  };

  const handleEditCategory = (category) => {
    setEditing(true);
    setCategory(category);
  };

  return (
    <div>
      <h2>Categories</h2>
      <form onSubmit={editing ? handleUpdateCategory : handleAddCategory}>
        <input type="text" name="name" value={category.name} onChange={handleInputChange} />
        <button type="submit">{editing ? 'Update' : 'Add'}</button>
      </form>
      <ul>
        {categories.map(category => (
          <li key={category.id}>
            {category.name}
            <button onClick={() => handleEditCategory(category)}>Edit</button>
            <button onClick={() => handleDeleteCategory(category.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Categories;
