import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import {NotificationManager } from 'react-notifications';


const MovieDetails = ({ movie }) => {
  const [movieDetails, setMovieDetails] = useState(movie);

  useEffect(() => {
    const fetchMovieDetails = async () => {
      if (!movie) {
        try {
          const response = await axios.get(`http://localhost:4000/movies/${movieId}`);
          NotificationManager.success('Категория успешно обновлена');
          setMovieDetails(response.data);
        } catch (error) {
          console.error(error);
         
        }
      }
    };

    fetchMovieDetails();
  }, [movie]);

  return (
    <div>
      <h2>{movieDetails.title}</h2>
      <p>{movieDetails.description}</p>
      <p>Category: {movieDetails.category && movieDetails.category.name}</p>
      <Link to="/movies" className="btn btn-primary">Back to Movies</Link>
    </div>
  );
};

export default MovieDetails;
