import { Link, Outlet, useNavigate, useParams, useLocation } from "react-router-dom";
import { useEffect,useState } from "react";
// import { ToastContainer, toast } from "react-toastify";
import { getOnePost, getAllUsers } from "./api";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function PostItem(){

    const location = useLocation();
    const navigate = useNavigate();
    const params = useParams();
    const [post, setPost] = useState([]);
    const [hasToken, setHasToken] = useState(false);
    const [users, setUser] = useState('');

    useEffect(() => {
        if(location.state){
           // toast(location.state.message);
            location.state = null;
        }

        const user = JSON.parse(localStorage.getItem("user"));
        if(user)
            setHasToken(true);
    }, [location]);

    useEffect(() => {
        const loadPost = async () => {
            const {success, data } = await getOnePost(params.postId);
            if(success){
                setPost(data);
            }
            else{
                navigate("/login", {state: { message: data }});
            }
        }
        loadPost();
    },[params.postId]);
    
    useEffect(()=>{
        const loadUsers = async () => {
            const {success, data } = await getAllUsers();
            if(success){
                setUser(data);
            }
            else{
                navigate("/login", {state: { message: data }});
            }
        }
        loadUsers();
    },[]);
    return(
        <div className="container">
            <div className="card">
                <div className="card-body">
                    <h5 className="card-title">{post.title}</h5>
                    <p className="card-text"><br/>Описание :{post.content}<br/><br/>Price: {post.price}<br/> </p>
                    <button className="btn btn-success">Buy</button>
                </div>
             </div>
        </div>
    );
}
