import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { NotificationManager } from 'react-notifications';

const MovieDetails = ({ movie, categoryId }) => {
  const [movieDetails, setMovieDetails] = useState(movie);
  const [category, setCategory] = useState(null);

  useEffect(() => {
    const fetchMovieDetails = async () => {
      if (!movie) {
        try {
          const response = await axios.get(`http://localhost:4000/movies/${movieId}`);
          setMovieDetails(response.data);
        } catch (error) {
          console.error(error);
        }
      }

      if (categoryId) {
        try {
          const response = await axios.get(`http://localhost:4000/categories/${categoryId}`);
          setCategory(response.data);
        } catch (error) {
          console.error(error);
        }
      }
    };

    fetchMovieDetails();
  }, [movie, categoryId]);

  return (
    <div>
      <h2>{movieDetails.title}</h2>
      <p>{movieDetails.description}</p>
      <p>Category: {category && category.name}</p>
      <Link to="/movies" className="btn btn-primary">Back to Movies</Link>
    </div>
  );
};

export default MovieDetails;
