import { Outlet } from "react-router-dom";
import { useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';  
import './Header.css'; // Import custom CSS

export default function Header() {
    const navigate = useNavigate();
    const loc = useLocation();

    const logoutForm = () => {
        console.log("logout");
        localStorage.removeItem("user");
        navigate("/login");
        window.location.reload();
    };

    useEffect(() => {
        if (loc.state) {
            toast(loc.state.message);
            loc.state = null;
        }

        const user = JSON.parse(localStorage.getItem("user"));
        // if(user)
        //     console.log("USER:" + user);
    }, [loc]);

    return (
        <div>
            <ToastContainer />
            <div className="container">  
                <nav className="navbar navbar-expand-lg navbar-light bg-light">  
                    <a className="navbar-brand" href="/">Home Page</a>
                    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarNav">
                        <ul className="navbar-nav mr-auto">
                            <li className="nav-item"><a className="nav-link" href="/login">Log In</a></li>
                            <li className="nav-item"><a className="nav-link" href="/register">Register</a></li>
                            <li className="nav-item"><a className="nav-link" href="/addproduct">Add Car</a></li>
                            <li className="nav-item"><a className="nav-link" href="//kolesa.group">Kolesa.kz</a></li>
                            <li className="nav-item"><a className="nav-link" href="/product">Cars</a></li>
                            <li className="nav-item"><a className="nav-link" href="/">Role</a></li>
                            <li className="nav-item">
                                <button className="btn btn-danger" onClick={logoutForm}>Logout</button>
                            </li>
                        </ul>
                    </div>
                </nav>
            </div>
            <Outlet />
        </div>
    );
}
