import { useNavigate, useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import { getOneProd, updateProduct, getCategory } from "./api";
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Edit() {
    const navigate = useNavigate();
    const { prodId } = useParams();
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState('');
    const [category, setCategory] = useState([]);
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
        async function fetchCategories() {
            const response = await getCategory();
            if (response.success) {
                setCategory(response.data);
            } else {
                toast.error("Failed to fetch categories");
            }
        }
        fetchCategories();
    }, []);

    useEffect(() => {
        async function fetchData() {
            const response = await getOneProd(prodId);
            if (response.success) {
                setName(response.data.name);
                setDescription(response.data.description);
                setPrice(response.data.price);
            } else {
                toast.error("Failed to fetch product details");
            }
        }
        fetchData();
    }, [prodId]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        const formData = { name, description, price, category };

        const { success, data } = await updateProduct(prodId, formData);
        setIsLoading(false);

        if (success) {
            toast.success("Product updated successfully!");
            navigate("/product");
        } else {
            toast.error(data || "Failed to update product");
        }
    };

    return (
        <div className="container mt-5">
            <ToastContainer />
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <h2 className="mb-3">Edit Product</h2>
                    <form onSubmit={handleSubmit} className="card p-4">
                        <div className="mb-3">
                            <label htmlFor="name" className="form-label">Name</label>
                            <input 
                                type="text" 
                                id="name" 
                                className="form-control" 
                                value={name} 
                                onChange={(e) => setName(e.target.value)} 
                                required 
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="description" className="form-label">Description</label>
                            <input 
                                type="text" 
                                id="description" 
                                className="form-control" 
                                value={description} 
                                onChange={(e) => setDescription(e.target.value)} 
                                required 
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="price" className="form-label">Price</label>
                            <input 
                                type="number" 
                                id="price" 
                                className="form-control" 
                                value={price} 
                                onChange={(e) => setPrice(e.target.value)} 
                                required 
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="category" className="form-label">Category</label>
                            <select 
                                id="category" 
                                className="form-control" 
                                value={category} 
                                onChange={(e) => setCategory(e.target.value)} 
                                required
                            >
                                {category.map(cat => (
                                    <option key={cat.id} value={cat.id}>{cat.name}</option>
                                ))}
                            </select>
                        </div>
                        <button type="submit" className="btn btn-primary" disabled={isLoading}>
                            {isLoading ? "Updating..." : "Update Product"}
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
}
