import React from 'react';

const ProductComp = ({ product }) => {
  if (!product) {
    return <div>Пожалуйста, введите ID продукта.</div>;
  }

  return (
    <div>
      <h2>{product.title}</h2>
      <p>{product.description}</p>
    </div>
  );
};

export default ProductComp;
