import React, { useState } from 'react';

const ColorInput = () => {
  const [hexColor, setHexColor] = useState('');
  const [imageData, setImageData] = useState(null);

  const fetchImage = async (hexColor) => {
    const response = await fetch(`https://php-noise.com/noise.php?hex=${hexColor}&json`);
    const data = await response.json();
    setImageData(data.noise);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const hexRegex = /^[0-9a-fA-F]{6}$/;
    if (hexRegex.test(hexColor)) {
      const encodedHexColor = hexColor === 'FFFFFF' ? 'АҚТҮС' : hexColor;
      fetchImage(encodedHexColor);
    } else {
      alert('Түстің алтылық мәнін 6 таңба ұзындығымен енгізіңіз.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Түстің алтылық мәнін енгізіңіз:
        <input
          type="text"
          value={hexColor}
          onChange={(e) => setHexColor(e.target.value.replace(/[^0-9a-fA-F]/g, ''))}
          pattern="[0-9a-fA-F]{6}"
          title="Түстің алтылық мәнін 6 таңба ұзындығымен енгізіңіз"
          maxLength={6}
        />
      </label>
      <button type="submit">Суретті алу</button>
      <ImageDisplay imageData={imageData} />
    </form>
  );
};

export default ColorInput;
