import { useState, useEffect } from "react";
import { deleteMovie, getOneMovie } from './api';
import { Link, useNavigate, useParams } from "react-router-dom";
// import {ToastContainer, toast} from 'react-toastify';
//import 'react-toastify/dist/ReactToastify.css';

function DeleteMovie(){

    const navigate = useNavigate();
    const params = useParams();
    const [movie, setMovie] = useState([]);

    useEffect(() => {
        const loadMovie = async () => {
            const {success, data} = await getOneMovie(params.movieId);
            if(success){
                setMovie(data);
            }
            else{
                navigate("/login");
            }
        }
        loadMovie();
    },[params.movieId]);

    const handleSubmit = async () => {
        const success = await deleteMovie(params.movieId);
        if(success){
            //toast("Ноут өшірілді!!");
            navigate('/movies/my');
        }
        // else{
        //     toast("movie error!!");
            
        // }
    };
    return(
        <div className="container">
            <h2>movie өшіріледі!!!</h2>
            {/* <ToastContainer/> */}
                <h4>{movie.title} өшеді???</h4>
                <button type="submit" className="btn btn-success" onClick={handleSubmit}>Ия</button> <br/>
            <Link to={'/movies/my'}>Жоқ</Link>
        </div>
    );
}

export default DeleteMovie;
