import axios from "axios";

const authHeader = () => {
    const user = JSON.parse(localStorage.getItem("user"));

    if(user && user.token){
        return { Authorization: 'Bearer ' + user.token }; 
    } else {
        return {};
    }
}

export const registerjs =async(userName,email,password)=>{
    try {
        const response = await axios.post("http://localhost:8091/auth/register",{
            userName:userName,
            userEmail: email,
            password: password
        })
        return {success: true, data:"User succes registred!!!"};
    } catch (error) {
        return {success: false, data: error.response.data.error};
    }
}

export const loginjs =async(userName,password)=>{
    try {
        const response = await axios.post("http://localhost:8091/auth/login",{
            userName: userName,
            password: password
        })
        localStorage.setItem("user",JSON.stringify(response.data));
        return {success: true, data:"Success logged in car shop"};
    } catch (error) {
        return {success: false, data: error.response.data};
    }
}
export const getProduct = async()=>{
    try{
        const response = await axios.get("http://localhost:8091/shop/product",{
        headers: authHeader()
    });
    return response.data;
    }catch(error){
        return error;
        // return { success: false, data: error.response.data.error };
    }
}

export const getCategory = async()=>{
    const response = await axios.get("http://localhost:8091/shop/category",{
        headers: authHeader()
    });
    return response.data;
}

export const getOneProd = async(id)=>{
    try{
        const response = await axios.get('http://localhost:8091/shop/product/'+id,{
            headers: authHeader()}
            
         );return response.data; 
    }
    catch(error){
        console.log(error);
    }}

export const Update = async(prod)=>{
    const response = await axios.put("http://localhost:8091/shop/product/update/"+prod.id, prod,{
        headers: authHeader()
    });
    return response.data;
}

export const saveProduct = async(formData)=>{
   try{
    const response = await axios.post("http://localhost:8091/shop/addproduct",formData,{
        headers: {
            ...authHeader(),
            'Content-Type': 'multipart/form-data'
          }
    });
    return response.data;
   }
   catch(e){
        console.log(e);
   }
}

export const saveCategory = async(category)=>{
    try{
     const response = await axios.post("http://localhost:8091/shop/addcategory",category,{
        headers: authHeader()
           }
     );
     return response.data;
    }
    catch(e){
         console.log(e);
    }
 }
