import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';  // Assuming you have AuthContext set up to manage authentication state

const HomePage = () => {
  const { isAuthenticated, logout } = useContext(AuthContext);
  const [loggedIn, setLoggedIn] = useState(isAuthenticated());

  const handleLogout = () => {
    logout(); // Clear the authentication token
    setLoggedIn(false);
  };

  return (
    <div className="container mt-5">
      <h1>Welcome to the Movie Management App!</h1>
      <p>Manage your movie collections and get detailed information about each movie here.</p>
      {loggedIn ? (
        <>
          <button onClick={handleLogout} className="btn btn-danger">Logout</button>
          <Link to="/movies" className="btn btn-primary ml-2">Go to Movies</Link>
          <br /><br />
          <Link to="/categories" className="btn btn-primary">Go to Categories</Link>
        </>
      ) : (
        <Link to="/login" className="btn btn-primary">Login</Link>
      )}
    </div>
  );
};

export default HomePage;
