import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { NotificationContainer, NotificationManager } from "react-notifications";
import { Routes, Route, Link, useParams, useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './css/style.css';

function App() {
  const [categories, setCategories] = useState([]);
  const [movies, setMovies] = useState([]);
  const { categoryId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchData() {
      const response = await axios.get('http://localhost:3000/categories');
      setCategories(response.data);
    }
    fetchData();
  }, []);

  useEffect(() => {
    async function fetchData() {
      const response = await axios.get('http://localhost:3000/movies');
      setMovies(response.data);
    }
    fetchData();
  }, []);

  async function saveMovie(movie) {
    await axios.post('http://localhost:3000/movies', movie);
    setMovies([...movies, movie]);
  }

  async function updateMovie(movie) {
    await axios.put(`http://localhost:3000/movies/${movie.id}`, movie);
    setMovies(movies.map(m => m.id === movie.id ? movie : m));
  }

  async function deleteMovie(id) {
    await axios.delete(`http://localhost:3000/movies/${id}`);
    setMovies(movies.filter(m => m.id !== id));
  }

  return (
    <div className="App">
      <nav>
        <ul>
          {categories.map(category => (
            <li key={category.id}>
              <Link to={`/category/${category.id}`}>{category.name}</Link>
            </li>
          ))}
        </ul>
      </nav>
      <Routes>
        <Route path="/category/:categoryId" element={<MovieList movies={movies} deleteMovie={deleteMovie} />} />
        <Route path="/add" element={<AddMovie categories={categories} saveMovie={saveMovie} />} />
        <Route path="/edit/:movieId" element={<EditMovie categories={categories} updateMovie={updateMovie} />} />
      </Routes>
      <NotificationContainer />
    </div>
  );
}

function MovieList({ movies, deleteMovie }) {
  const { categoryId } = useParams();
  const filteredMovies = categoryId ? movies.filter(m => m.categoryId === parseInt(categoryId)) : movies;

  return (
    <ul>
      {filteredMovies.map(movie => (
        <li key={movie.id}>
          {movie.title}
          <button onClick={() => deleteMovie(movie.id)}>Delete</button>
          <Link to={`/edit/${movie.id}`}>Edit</Link>
        </li>
      ))}
    </ul>
  );
}

function AddMovie({ categories, saveMovie }) {
  const [movie, setMovie] = useState({});

  function handleSubmit(e) {
    e.preventDefault();
    saveMovie(movie);
    navigate('/');
  }

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" placeholder="Title" onChange={e => setMovie({ ...movie, title: e.target.value })} />
      <select onChange={e => setMovie({ ...movie, categoryId: parseInt(e.target.value) })}>
        {categories.map(category => (
          <option key={category.id} value={category.id}>{category.name}</option>
        ))}
      </select>
      <button type="submit">Save</button>
    </form>
  );
}

function EditMovie({ categories, updateMovie, movieId }) {
  const [movie, setMovie] = useState({});

  useEffect(() => {
    async function fetchData() {
      const response = await axios.get(`http://localhost:3000/movies/${movieId}`);
      setMovie(response.data);
    }
    fetchData();
  }, [movieId]);

  function handleSubmit(e) {
    e.preventDefault();
    updateMovie(movie);
    navigate('/');
  }

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={movie.title} placeholder="Title" onChange={e => setMovie({ ...movie, title: e.target.value })} />
      <select value={movie.categoryId} onChange={e => setMovie({ ...movie, categoryId: parseInt(e.target.value) })}>
        {categories.map(category => (
          <option key={category.id} value={category.id}>{category.name}</option>
        ))}
      </select>
      <button type="submit">Save</button>
    </form>
  );
}

export default App;
