import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';
import MovieDetails from '../components/MovieDetails';

const MovieDetailsPage = () => {
  const [movie, setMovie] = useState(null);
  const { movieId, categoryId } = useParams();

  useEffect(() => {
    const fetchMovieDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/movies/${movieId}`);
        const movieDetails = response.data;
        if (categoryId) {
          const categoryResponse = await axios.get(`http://localhost:4000/categories/${categoryId}`);
          movieDetails.category = categoryResponse.data;
        }
        setMovie(movieDetails);
      } catch (error) {
        console.error(error);
      }
    };

    fetchMovieDetails();
  }, [movieId, categoryId]);

  if (!movie) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{movie.title}</h2>
      <MovieDetails movie={movie} />
      <Link to="/movies" className="btn btn-primary">Back to Movies</Link>
    </div>
  );
};

export default MovieDetailsPage;
