import React, { useState } from 'react';

const ColorInput = ({ onSubmit }) => {
  const [hexColor, setHexColor] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    const hexRegex = /^[0-9a-fA-F]{6}$/;
    if (hexRegex.test(hexColor)) {
      onSubmit(hexColor);
    } else {
      alert('Пожалуйста, введите шестнадцатеричное значение цвета длиной 6 символов.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Введите шестнадцатеричное значение цвета:
        <input
          type="text"
          value={hexColor}
          onChange={(e) => setHexColor(e.target.value.replace(/[^0-9a-fA-F]/g, ''))}
          pattern="[0-9a-fA-F]{6}"
          title="Введите шестнадцатеричное значение цвета (6 символов)"
          maxLength={6}
        />
      </label>
      <button type="submit">Получить изображение</button>
    </form>
  );
};

export default ColorInput;
