import { Link, Outlet, useNavigate, useParams } from "react-router-dom";
import { useEffect,useState } from "react";
import { getCategoryMovies } from "./api";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function CategoryMovies(){

    const navigate = useNavigate();
    const params = useParams();
    const [movies, setMovies] = useState([]);

    useEffect(() => {
        const loadMovies = async () => {
            const {success, data } = await getCategoryMovies(params.catId);
            if(success){
                setMovies(data);
            }
            else{
                navigate("/login");
            }
        }
        loadMovies();
    },[params]);

    return(
        <div className="container">
            <div className="row">
                    {
                        movies.map((movie) => {
                            return(
                                <div className="col-sm-6" key={movie.id}>
                                    <div className="card mt-4 py-3 mx-5" >
                                        <div className="card-body">
                                            <h5 className="card-title"><Link className="nav-link" to={'/movies/'+movie.id} >{movie.title}</Link></h5>
                                            <p className="card-text"> Price : { movie.price}</p>
                                        </div>
                                    </div>
                                </div>
                            )
                        })
                    }
                </div>
            <div>
                <Outlet/>
            </div>
        </div>
    )
};
