import React, { useState } from 'react';

const App = ({ films }) => {
  const [selectedFilm, setSelectedFilm] = useState(null);

  const handleFilmClick = (film) => {
    setSelectedFilm(film);
  };

  return (
    <div>
      {films.map((film, index) => (
        <p key={index} onClick={() => handleFilmClick(film)}>
          {film.name}
        </p>
      ))}

      {selectedFilm && (
        <div>
          <input type="text" value={selectedFilm.name} readOnly />
          <input type="text" value={selectedFilm.year} readOnly />
          <input type="text" value={selectedFilm.author} readOnly />
        </div>
      )}
    </div>
  );
};

const films = [
  { name: 'Film 1', year: 2000, author: 'Author 1' },
  { name: 'Film 2', year: 2005, author: 'Author 2' },
  { name: 'Film 3', year: 2010, author: 'Author 3' },
];

export default App;
