import axios from "axios";

const BASE_URL = "http://localhost:8082";

const authHeader = () => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (user && user.token) {
        return { Authorization: 'Bearer ' + user.token };
    } else {
        return {};
    }
}

export const registerjs = async (userName, email, password) => {
    try {
        const response = await axios.post(`${BASE_URL}/auth/register`, {
            userName: userName,
            userEmail: email,
            password: password
        });
        return { success: true, data: "User successfully registered!!!" };
    } catch (error) {
        return { success: false, data: error.response.data.error };
    }
}

export const loginjs = async (userName, password) => {
    try {
        const response = await axios.post(`${BASE_URL}/auth/login`, {
            userName: userName,
            password: password
        });
        localStorage.setItem("user", JSON.stringify(response.data));
        return { success: true, data: "Successfully logged in" };
    } catch (error) {
        return { success: false, data: error.response.data };
    }
}

export const getProduct = async () => {
    try {
        const response = await axios.get(`${BASE_URL}/shop/product`, {
            headers: authHeader()
        });
        return response.data;
    } catch (error) {
        return error;
    }
}

export const getCategory = async () => {
    const response = await axios.get(`${BASE_URL}/shop/category`, {
        headers: authHeader()
    });
    return response.data;
}

export const getOneProd = async (id) => {
    try {
        const response = await axios.get(`${BASE_URL}/shop/product/${id}`, {
            headers: authHeader()
        });
        return response.data;
    } catch (error) {
        console.log(error);
    }
}

export const Update = async (prod) => {
    const response = await axios.put(`${BASE_URL}/shop/product/update/${prod.id}`, prod, {
        headers: authHeader()
    });
    return response.data;
}

export const saveProduct = async (formData) => {
    try {
        const response = await axios.post(`${BASE_URL}/shop/addproduct`, formData, {
            headers: {
                ...authHeader(),
                'Content-Type': 'multipart/form-data'
            }
        });
        return response.data;
    } catch (e) {
        console.log(e);
    }
}

export const saveCategory = async (category) => {
    try {
        const response = await axios.post(`${BASE_URL}/shop/addcategory`, category, {
            headers: authHeader()
        });
        return response.data;
    } catch (e) {
        console.log(e);
    }
}
