import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import MovieForm from '../components/MovieForm';
import MovieList from '../components/MovieList';

const MoviesPage = () => {
  const [category, setCategory] = useState(null);

  return (
    <div>
      <h2>Movies</h2>
      <Link to="/categories" className="btn btn-primary mb-2">Categories</Link>
      <MovieForm category={category} />
      <MovieList categoryId={category?.id} />
      {categories.map((category) => (
        <div key={category.id}>
          <button onClick={() => setCategory(category)} className="btn btn-secondary mr-1">{category.name}</button>
        </div>
      ))}
    </div>
  );
};

export default MoviesPage;
