import React, { useState } from 'react';
import SearchComp from './SearchComp';
import FactList from './FactList';

const App = () => {
  const [facts, setFacts] = useState([]);

  const fetchFacts = async (count, lang) => {
    const response = await fetch(`https://meowfacts.herokuapp.com/?count=${count}&lang=${lang}`);
    const data = await response.json();
    setFacts(data.data);
  };

  const handleSearch = (count, lang) => {
    fetchFacts(count, lang);
  };

  return (
    <div>
      <SearchComp onSearch={handleSearch} />
      <FactList facts={facts} />
    </div>
  );
};

export default App;
