import { Outlet } from "react-router-dom";
import { useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Header() {
    const navigate = useNavigate();
    const loc = useLocation();

    const logoutForm = () => {
        console.log("logout");
        localStorage.removeItem("user");
        navigate("/login");
        window.location.reload();
    };

    useEffect(() => {
        if (loc.state) {
            toast(loc.state.message);
            loc.state = null;
        }
    }, [loc]);

    return (
        <div>
            <ToastContainer />
            <div className="container">
                <nav className="navbar navbar-expand-lg navbar-light bg-light">
                    <a className="navbar-brand" href="/">Home page</a>
                    <div className="collapse navbar-collapse">
                        <ul className="navbar-nav mr-auto">
                            <li className="nav-item"><a className="nav-link" href="/login">log in</a></li>
                            <li className="nav-item"><a className="nav-link" href="/register">Register</a></li>
                            <li className="nav-item"><a className="nav-link" href="/addproduct">Add Product</a></li>
                            <li className="nav-item"><a className="nav-link" href="/product">Products</a></li>
                            <button className="btn btn-danger" onClick={logoutForm}>Logout</button>
                        </ul>
                    </div>
                </nav>
            </div>
            <Outlet />
        </div>
    );
}
