import { useState } from "react";
import { saveCategory } from "./api";
import 'bootstrap/dist/css/bootstrap.min.css'; 

export default function CreateCat() {
    const [category, setCategory] = useState({ name: '' });

    const handleName = (ev) => {
        setCategory({ ...category, name: ev.target.value });
    }

    const handleForm = async (ev) => {
        ev.preventDefault();
        try {
            const response = await saveCategory(category);
            console.log('Response:', response);
            setCategory({ name: '' }); 
        } catch (error) {
            console.log("Error:", error);
        }
    }

    return (
        <div className="container mt-4">
            <form className="form-container" onSubmit={handleForm}>
                <div className="mb-3">
                    <label htmlFor="name" className="form-label">Name:</label>
                    <input type="text" id="name" name="name" className="form-control" value={category.name} onChange={handleName} required />
                </div>

                <button type="submit" className="btn btn-primary">Add</button>
            </form>
        </div>
    );
}
