import { useState, useEffect } from "react";
import { setPost,getAllCategories } from './api';
import { Link, useNavigate } from "react-router-dom";
import {ToastContainer, toast} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function CreatePost(){

    const navigate = useNavigate();

    const [title, settitle] = useState('');
    const [price, setPrice] = useState('');
    const [content, setContent] = useState('');
    const [categories, setCategories] = useState([]);

    const handleSubmit = async (e) => {
        const {success, data} = await setPost(title, price, content, categories);
        if(success){
            navigate('/posts/my', {state: {message: "Пост қосылды!!!"}});
        }
        // else{
        //     toast("Post qosylmady");
            
        // }
    };

    useEffect(() => {
        const loadCat = async () => {
            const categories = await getAllCategories();
            setCategories(categories);
        }
        loadCat();
    },[]);

    return(
        <div className="container">
            <h2>Создать кино</h2>
            { <ToastContainer/> }
                <label>Заглавие:</label><br/>
                <input type="text" className="form-control" value={title} onChange={(e) => settitle(e.target.value)} /><br/>
                <label>Цена:</label><br/>
                <input type="text" className="form-control" value={price} onChange={(e) => setPrice(e.target.value)} /><br/>
                <label>Описание:</label><br/>
                <input type="text" className="form-control" value={content} onChange={(e) => setContent(e.target.value)} /><br/>
                <label>Категория</label><br/>
                <select className="form-control" onChange={(e) => setCategories(e.target.value)}>
                    <option value={1}>Комедия</option>
                    <option value={2}>Боевик</option>
                    <option value={3}>Ужасы</option>
                </select><br/><br/>
                <button type="submit" onClick={handleSubmit} className="btn btn-primary">Created</button>
        </div>
    );
}

export default CreatePost;
