import React, { useState } from 'react';

const movies = [
  { name: 'Temirlan', year: 2000, author: 'Director 1' },
  { name: 'Zhanserik', year: 2005, author: 'Director 2' },
  { name: 'Erejep', year: 2010, author: 'Director 3' },
  { name: 'Almat', year: 2015, author: 'Director 4' },
  { name: 'Kydyrali', year: 2020, author: 'Director 5' },
  { name: 'Aikumis', year: 2025, author: 'Director 6' },
];

const MovieList = () => {
  const [selectedMovie, setSelectedMovie] = useState(null);

  const handleMovieClick = (movie) => {
    setSelectedMovie(movie);
  };

  return (
    <div>
      {movies.map((movie, index) => (
        <div key={index} onClick={() => handleMovieClick(movie)}>
          {movie.name}
        </div>
      ))}
      <hr />
      <div>
        <input type="text" value={selectedMovie?.name || ''} readOnly />
        <input type="text" value={selectedMovie?.year || ''} readOnly />
        <input type="text" value={selectedMovie?.author || ''} readOnly />
      </div>
    </div>
  );
};

export default MovieList;
