import React, { useState } from "react";
import InputComp from "./InputComp";
import BreweryListComp from "./BreweryListComp";

const App = () => {
  const [breweries, setBreweries] = useState([]);

  const fetchBreweries = async (count) => {
    const url = `https://api.openbrewerydb.org/v1/breweries/random?size=${count}`;
    const response = await fetch(url);
    const data = await response.json();
    setBreweries(data);
  };

  return (
    <div>
      
      <InputComp onSearch={fetchBreweries} />
      <BreweryListComp breweries={breweries} />
    </div>
  );
};

export default App;
