import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import { NotificationContainer,NotificationManager } from 'react-notifications';

const CategoryForm = () => {
  const [name, setName] = useState('');
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCategory = async () => {
      
      if (id) {
        try {
          const response = await axios.get(`http://localhost:4000/categories/${id}`);
          setName(response.data.name);
        } catch (error) {
          console.error(error);
          NotificationManager.error('Ошибка при выборе категории');
        }
      }
      
    };

    fetchCategory();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (id) {
      // Update category
      try {
        await axios.put(`http://localhost:4000/categories/${id}`, { name });
        NotificationManager.success('Категория успешно обновлена');
        navigate('/categories');
      } catch (error) {
        console.error(error);
        NotificationManager.error('Категория обновления ошибок');
      }
    } else {
      // Create category
      try {
        await axios.post('http://localhost:4000/categories', { name });
        NotificationManager.success('Категория успешно создана');
        navigate('/categories');
      } catch (error) {
        console.error(error);
        NotificationManager.error('Ошибка при создании категории');
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label htmlFor="name">Name</label>
        <input type="text" className="form-control" id="name" value={name} onChange={(e) => setName(e.target.value)} required />
      </div>
      <button type="submit" className="btn btn-danger ">Save</button>
    </form>
  );
};

export default CategoryForm;
