import { Link, Outlet, useNavigate } from "react-router-dom";
import { useEffect,useState } from "react";
import { getMyPosts } from "./api";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function MyPosts(){

    const navigate = useNavigate();
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        const loadPosts = async () => {
            const {success, data } = await getMyPosts();
            if(success){
                setPosts(data);
            }
            else{
                navigate("/login", {state: { message: "Пост жоқ!!!" }});
            }
        }
        loadPosts();
    },[]);

    return(
        <div className="container">
            <div className="d-flex justify-content-around">
                {
                    posts.map((p) => {
                        return(
                            <div className="card mt-4 py-3"  key={p.id}>
                                <div className="card-body">
                                    <h5 className="card-title"><Link className="nav-link" to={'/posts/'+p.id} >{p.title}</Link></h5>
                                    <Link to={'/posts/'+p.id+'/edit'} className="btn btn-primary">Edit</Link> &nbsp;
                                    <Link to={'/posts/'+p.id+'/delete'} className="btn btn-danger">Delete</Link>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
            <Link to={'/posts/create'} className="btn btn-warning mx-5">Добавить Кино</Link>
            <div>
                <Outlet/>
            </div>
        </div>
    )
};
