import { useState, useEffect } from "react";
import { getOneProd, updateProduct, getCategory } from "./api";
import { useParams } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';

export default function UpdateProduct() {
    const { id } = useParams();
    const [prod, setProd] = useState({
        name: '',
        price: 0,
        category: ''
    });
    const [category, setCategory] = useState([]);
    
    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const product = await getOneProd(id);
                setProd(product);
            } catch (error) {
                console.error("Error fetching product:", error);
            }
        };
        const fetchCategories = async () => {
            try {
                const categories = await getCategory();
                setCategory(categories);
            } catch (error) {
                console.error("Error fetching categories:", error);
            }
        };
        fetchProduct();
        fetchCategories();
    }, [id]);

    const handleName = (ev) => {
        setProd({ ...prod, name: ev.target.value });
    }
    const handlePrice = (ev) => {
        setProd({ ...prod, price: ev.target.value });
    }
    const handleCategory = (ev) => {
        setProd({ ...prod, category: ev.target.value });
    }

    const handleForm = async (ev) => {
        ev.preventDefault();
        try {
            const response = await updateProduct(id, prod);
            console.log('Response:', response);
        } catch (error) {
            console.log("Error:", error);
        }
    }

    // Ensure prod is loaded before rendering the form
    if (!prod) {
        return <div>Loading...</div>;
    }

    return (
        <div className="container mt-4">
            <form onSubmit={handleForm}>
                <div className="mb-3">
                    <label htmlFor="name" className="form-label">Name:</label>
                    <input type="text" id="name" className="form-control" value={prod.name || ''} onChange={handleName} />
                </div>
                <div className="mb-3">
                    <label htmlFor="price" className="form-label">Price:</label>
                    <input type="number" id="price" className="form-control" value={prod.price || 0} onChange={handlePrice} />
                </div>
                <div className="mb-3">
                    <label htmlFor="category" className="form-label">Category:</label>
                    <select id="category" className="form-control" value={prod.category || ''} onChange={handleCategory}>
                        {category.map((op) => (
                            <option key={op.id} value={op.id}>{op.name}</option>
                        ))}
                    </select>
                </div>
                <button type="submit" className="btn btn-primary">Update</button>
            </form>
        </div>
    );
}
